const app = props => {

  useState({
    persons: [
      { name: 'Ahsan', age: '25' },
      { name: 'Taha', gender: 'Man', age: '20' },
      { name: 'Areeb', age: '27' },
    ]
  });

  return (
    <div className="App">
      <h1>Hi I am a React app function </h1>
      <button onClick={this.SwitchNameHandler}>Switch Name</button>
      <Person name={this.state.persons[0].name} gender='Male' age={this.state.persons[0].age} />
      <Person name={this.state.persons[1].name} gender={this.state.persons[1].gender} age={this.state.persons[1].age}>Hobbies: Not working!!!</Person>
      <Person name={this.state.persons[2].name} gender='Male' age={this.state.persons[2].age} />
    </div>
  );
}

export default app;



